mapper.select:
select    from user_message
where 1 = 1

	and senduser =#{senduser}


	and receiveuser =#{receiveuser}


	and isread =#{isread}


	and groupid =#{groupid}


	and type =#{type}


	and type in

		#{id}


order by id desc

select id, receiveuser as uid, content, createdate, 1 as tp, '' as href, '' as remark, 1 as rd, null as fm, null as from_group, CASE WHEN TIMESTAMPDIFF(DAY, createdate,NOW()) > 0 THEN CONCAT(TIMESTAMPDIFF(DAY, createdate,NOW()),'天前') WHEN TIMESTAMPDIFF(HOUR, createdate,NOW()) > 0 THEN CONCAT(TIMESTAMPDIFF(HOUR, createdate,NOW()),'小时前') WHEN TIMESTAMPDIFF(MINUTE, createdate,NOW()) >= 5 THEN CONCAT(TIMESTAMPDIFF(MINUTE, createdate,NOW()),'分前') WHEN TIMESTAMPDIFF(MINUTE, createdate,NOW()) >= 0 and TIMESTAMPDIFF(MINUTE, createdate,NOW()) < 5 THEN '刚刚' end AS 'time' from user_message where 1 = 1 and (type = 2
and senduser =#{senduser}
and receiveuser =#{receiveuser}
and isread =#{isread}
and groupid =#{groupid}
and type =#{type}
) or type = 3 order by id desc

select count(1) from user_message
where 1 = 1

	and senduser =#{senduser}


	and receiveuser =#{receiveuser}


	and isread =#{isread}


	and groupid =#{groupid}


	and type =#{type}


	and type in

		#{id}

select count(1) from user_message
where 1 = 1 and (type = 2

	and senduser =#{senduser}


	and receiveuser =#{receiveuser}


	and isread =#{isread}


	and groupid =#{groupid}


	and type =#{type}

)
or type = 3

mapper.update:
update user_info
lastondate = now(), lastoffdate = now(), lastupdatedate = now()
where uid = #{uid}
